void main()
{
   int count=0,coin1,coin2,coin5;
   for(coin5=0;coin5<=20;coin5++)
      for(coin2=0;coin2<=50;coin2++)
          for(coin1=0;coin1<=100;coin1++)
          {
          if(coin1+2*coin2+5*coin5=100&&coin1+coin2+coin5=50)
          count++;
          else count=count;break;
         }
printf("第%d种，1分有%d个，2分有%d个，5分有%d个\n"，count,coin1,coin2,coin5);
}

#include<stdio.h>
void main()
{
   int n,m,f,sum;
   printf("请输入苹果的个数");
   scanf("%d",&n);
   for(m=0;m<=(n/2);m++)
     for(f=0;f<=n;f++)
        {
        if(2*m+f=n)  sum++;
        else   sum=sum;
        }
   printf("小刚吃完%d个苹果有%d种方法"，n,sum);
}
