#include<stdio.h>
#include<stdlib.h>
int main()
{
	int a[1001];
	int i,j,t;

	for(i=0;i<1000;i++)
		scanf("%d",a[i]);

	for(i=0;i<999;i++)
		for(j=i+1;j<1000;j++)
		{
			if(a[i]>a[j])
			{
			t=a[i];
			a[i]=a[j];
			a[j]=t;
			}
		}
	for(i=0;i<1000;i++)
		printf("%d",a[i]);
	return 0;
}
